import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { CustomSpinner } from "../loading/Loader";
import { ErrorAlert } from "../error/Error";
import { UserCards } from "../card/UserCard";
import { getAllWorks, selectWorks, selectIsLoading, selectError } from "../../redux/worksSlice";
import styles from "./workContent.module.css";
import { DefaultPagination } from "../pagination/Pagination";

export const WorksContent = () => {
  const dispatch = useDispatch();
  const isLoading = useSelector(selectIsLoading);
  const error = useSelector(selectError);
  const works = useSelector(selectWorks); // Stato degli works estratto dallo slice
  const [page, setPage] = useState(1);

  useEffect(() => {
    dispatch(getAllWorks(page)); // Chiamata alla funzione di azione con il numero di pagina
  }, [dispatch, page]);

  const onPageChange = (newPage) => {
    setPage(newPage);
  };

  return (
    <div className={styles.content}>
      {isLoading && <CustomSpinner />}
      {!isLoading && error && <ErrorAlert message="Ops! Qualcosa è andato storto" />}
      {!isLoading && !error && (
        works.map((work) => (
          <div key={work._id}>
            <UserCards
              className={`${styles.card} size-24`}
              author={work.author}
              description={work.description}
              title={work.title}
              img={work.img}
              location={work.location}
              pubDate={work.pubDate}
            />
          </div>
        ))
      )}

      <DefaultPagination
        className={styles.pagination}
        onPageChange={onPageChange}
        currentPage={page}
        totalPage={Math.ceil(works.totalWorks / works.pageSize)} // Calcola il numero totale di pagine
      />
    </div>
  );
};
