import { jwtDecode } from "jwt-decode";

const session = jwtDecode(localStorage.getItem("auth"));

export const sessionData = {
    email: session.email,
    firstName: session.firstName,
    lastName: session.lastName,
    role: session.role,
    _id: session._id,
  };
