import { createAsyncThunk, createSlice } from "@reduxjs/toolkit";
import AxiosClient from "../client/client";
const client = new AxiosClient();

const initialState = {
  works: [],
  isLoading: false,
  error: null,
  totalWorks: 0,
  currentPage: 1, // Aggiunto lo stato per la pagina corrente
  pageSize: 4,   // Aggiunto lo stato per le dimensioni della pagina
};

export const getAllWorks = createAsyncThunk(`work/GETWORKS`, async (_, { getState }) => {
  const { currentPage, pageSize } = getState().works; // Ottieni lo stato corrente dei parametri di paginazione
  const queryParams = `?page=${currentPage}&pageSize=${pageSize}`; // Costruisci i query parameters
  return await client.get(`/works${queryParams}`); // Esegui la richiesta GET con i query parameters
});

const worksSlice = createSlice({
    name: "works",
    initialState,
    extraReducers: (builder) => {
      builder
        .addCase(getAllWorks.pending, (state) => {
          state.isLoading = true;
        })
        .addCase(getAllWorks.fulfilled, (state, action) => { 
          state.isLoading = false;
          state.totalWorks = action.payload.data.totalWorks;
          state.works = action.payload.data.payload;
        })
        .addCase(getAllWorks.rejected, (state) => {
          state.isLoading = false;
          state.error = "Ops, an error has occurred.";
        });
    },
});

export const selectWorks = (state) => state.works.works;
export const selectIsLoading = (state) => state.works.isLoading;
export const selectError = (state) => state.works.error;
export const selectTotalWorks = (state) => state.works.totalWorks;
export const selectCurrentPage = (state) => state.works.currentPage;
export const selectPageSize = (state) => state.works.pageSize;

export default worksSlice.reducer;
