import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { CustomSpinner } from "../loading/Loader";
import { ErrorAlert } from "../error/Error";
import { UserCards } from "../card/UserCard";
import { getAllWorks, selectWorks, selectIsLoading, selectError, selectCurrentPage, selectPageSize, selectTotalWorks } from "../../redux/WorkCardSlice";
import styles from "./workContent.module.css";
import { DefaultPagination } from "../pagination/Pagination";

export const WorksContent = () => {
  const dispatch = useDispatch();
  const works = useSelector(selectWorks);
  const isLoading = useSelector(selectIsLoading);
  const error = useSelector(selectError);
  const currentPage = useSelector(selectCurrentPage);
  const pageSize = useSelector(selectPageSize);
  const totalWorks = useSelector(selectTotalWorks);

  useEffect(() => {
    dispatch(getAllWorks());
  }, [dispatch]);

  const onPageChange = (page) => {
    dispatch(getAllWorks({ page }));
  };

  return (
    <div className={styles.content}>
      {isLoading && <CustomSpinner />}
      {!isLoading && error && <ErrorAlert message="Ops! qualcosa è andato storto" />}
      {!isLoading && !error && (
        works &&
        works.map((work) => (
          <div key={work._id}>
            <UserCards
              className={`${styles.card} size-24`}
              author={work.author}
              description={work.description}
              title={work.title}
              img={work.img}
              location={work.location}
              pubDate={work.pubDate}
            />
          </div>
        ))
      )}
      <DefaultPagination
        currentPage={currentPage}
        pageSize={pageSize}
        totalPage={Math.ceil(totalWorks / pageSize)}
        onPageChange={onPageChange}
      />
    </div>
  );
};
