import { createAsyncThunk, createSlice } from "@reduxjs/toolkit";
import axios from "axios";
import { jwtDecode } from "jwt-decode";

// Funzione per ottenere il token JWT dall'archivio locale
const getToken = () => localStorage.getItem("auth");

// Middleware per includere il token JWT nelle intestazioni delle richieste
axios.interceptors.request.use((config) => {
  const token = getToken();
  if (token) {
    config.headers.Authorization = `Bearer ${token}`;
  }
  return config;
});

// Funzione per decodificare il token JWT e restituire i dati utente
const decodeToken = (token) => {
  if (token) {
    const decodedToken = jwtDecode(token);
    return decodedToken;
  }
  return null;
};

// Azione asincrona per recuperare i dati utente
export const fetchUserData = createAsyncThunk(
  "user/fetchUserData",
  async (_, thunkAPI) => {
    try {
      const token = getToken();
      if (!token) {
        throw new Error("Token not found");
      }
      const decodedToken = decodeToken(token);
      if (!decodedToken) {
        throw new Error("Invalid token");
      }
      // Qui sostituisci l'URL con l'endpoint corretto per ottenere i dati utente
      const response = await axios.get(`http://localhost:4040/users/${decodedToken.userId}`);
      return response.data;
    } catch (error) {
      return thunkAPI.rejectWithValue(error.message);
    }
  }
);

// Stato iniziale dello slice
const initialState = {
  user: null,
  isLoading: false,
  error: null,
};

// Slice per gestire lo stato degli utenti
const userSlice = createSlice({
  name: "user",
  initialState,
  reducers: {},
  extraReducers: (builder) => {
    builder.addCase(fetchUserData.pending, (state) => {
      state.isLoading = true;
      state.error = null;
    });
    builder.addCase(fetchUserData.fulfilled, (state, action) => {
      state.isLoading = false;
      state.user = action.payload;
    });
    builder.addCase(fetchUserData.rejected, (state, action) => {
      state.isLoading = false;
      state.error = action.payload;
    });
  },
});

// Selettore per ottenere i dati dell'utente dallo stato
export const selectUser = (state) => state.user;

// Esporta il riduttore dello slice
export default userSlice.reducer;
