import { jwtDecode } from "jwt-decode";

let sessionData = {};

const session = localStorage.getItem("auth");

if (session) {
  const decodedSession = jwtDecode(session);

  sessionData = {
    firstName: decode.firstName,
    lastName: decode.lastName,
    email: decode.email,
    role: decode.role,
    _id: decode._id,
    preferWorks: decode.preferWorks ? decode.preferWorks : undefined,
    myWorks: decode.myWorks ? decode.myWorks : undefined,
  };
}

export default sessionData;
