import React from "react";
import { Button, IconButton } from "@material-tailwind/react";
import { ArrowRightIcon, ArrowLeftIcon } from "@heroicons/react/24/outline";

export function DefaultPagination({ currentPage, page, pageSize, totalPage, onPageChange }) {
  const [active, setActive] = React.useState(currentPage);

  const getItemProps = (index) => ({
    variant: active === index ? "filled" : "text",
    color: "gray",
    onClick: () => {
      setActive(index);
      onPageChange(index); // Chiamata alla funzione di gestione del cambio pagina
    },
  });

  const next = () => {
    if (active === totalPage) return;

    const nextPage = active + 1;
    setActive(nextPage);
    onPageChange(nextPage); // Chiamata alla funzione di gestione del cambio pagina
  };

  const prev = () => {
    if (active === 1) return;

    const prevPage = active - 1;
    setActive(prevPage);
    onPageChange(prevPage); // Chiamata alla funzione di gestione del cambio pagina
  };

  return (
    <div className="flex items-center gap-4">
      <Button
        variant="text"
        className="flex items-center gap-2"
        onClick={prev}
        disabled={active === 1}
      >
        <ArrowLeftIcon strokeWidth={2} className="h-4 w-4" /> Previous
      </Button>
      <div className="flex items-center gap-2">
        {[...Array(totalPage)].map((_, index) => (
          <IconButton key={index} {...getItemProps(index + 1)}>
            {index + 1}
          </IconButton>
        ))}
      </div>
      <Button
        variant="text"
        className="flex items-center gap-2"
        onClick={next}
        disabled={active === totalPage}
      >
        Next <ArrowRightIcon strokeWidth={2} className="h-4 w-4" />
      </Button>
    </div>
  );
}
