import React, { useEffect } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import { fetchUserData, selectUser } from "../../redux/UserSlice";
import { Navbar, Typography, Button, IconButton, Dialog } from "@material-tailwind/react";
import { LoginForm } from "../loginForm/LoginForm";

export function StickyNavbar() {
  const [openNav, setOpenNav] = React.useState(false);
  const [open, setOpen] = React.useState(false);
  const dispatch = useDispatch();
  const user = useSelector(selectUser);
  const location = useLocation();
  const navigate = useNavigate();

  useEffect(() => {
    // Esegui la richiesta per recuperare i dati dell'utente solo se l'utente è sulla propria pagina
    if (location.pathname === "/your-page") {
      dispatch(fetchUserData());
    }
  }, [dispatch, location.pathname]);

  const handleOpen = () => setOpen(!open);
  const handleLogOut = () => {
    localStorage.clear();
    navigate("/");
  };
  const handleHome = () => {
    navigate("/");
  };

  React.useEffect(() => {
    window.addEventListener(
      "resize",
      () => window.innerWidth >= 960 && setOpenNav(false)
    );
  }, []);

  return (
    <div className="-m-6 max-h-[768px] w-[calc(100%+48px)] overflow-scroll">
      <Navbar className="sticky top-0 z-10 h-max max-w-full rounded-none px-4 py-2 lg:px-8 lg:py-4">
        <div className="flex items-center justify-between text-blue-gray-900">
          <Typography
            onClick={handleHome}
            as="a"
            href=" "
            className="mr-4 cursor-pointer py-1.5 font-medium"
          >
            Just Do It
          </Typography>
          <div className="flex items-center gap-4">
            <div>
              {user && (
                <div>
                  <h2>{`Welcome, ${user.firstname} ${user.lastname}`}</h2>
                  <p>{`Email: ${user.email}`}</p>
                </div>
              )}
            </div>
            <div className="flex items-center gap-x-1">
              <div className="flex items-center gap-x-1">
                <Button onClick={handleLogOut} variant="gradient">
                  Log out
                </Button>
                <Button onClick={handleOpen} variant="gradient">
                  Log in
                </Button>
                <Dialog
                  open={open}
                  handler={handleOpen}
                  className="flex justify-center"
                >
                  <LoginForm />
                </Dialog>
              </div>
            </div>
            <IconButton
              variant="text"
              className="ml-auto h-6 w-6 text-inherit hover:bg-transparent focus:bg-transparent active:bg-transparent lg:hidden"
              ripple={false}
              onClick={() => setOpenNav(!openNav)}
            >
              {openNav ? (
                <svg
                  xmlns="http://www.w3.org/2000/svg"
                  fill="none"
                  className="h-6 w-6"
                  viewBox="0 0 24 24"
                  stroke="currentColor"
                  strokeWidth={2}
                >
                  <path
                    strokeLinecap="round"
                    strokeLinejoin="round"
                    d="M6 18L18 6M6 6l12 12"
                  />
                </svg>
              ) : (
                <svg
                  xmlns="http://www.w3.org/2000/svg"
                  className="h-6 w-6"
                  fill="none"
                  stroke="currentColor"
                  strokeWidth={2}
                >
                  <path
                    strokeLinecap="round"
                    strokeLinejoin="round"
                    d="M4 6h16M4 12h16M4 18h16"
                  />
                </svg>
              )}
            </IconButton>
          </div>
        </div>
      </Navbar>
    </div>
  );
}
