const useAuth = () => {
    const token = localStorage.getItem("auth");
    try {
        if (token) {
            return jwtDecode(token);
        } else {
            return null;
        }
    } catch (error) {
        console.error("Failed to decode token:", error);
        return null;
    }
};

export const ProtectedRoutes = () => {
    const isAuth = useAuth();
    return isAuth ? <Outlet /> : <HomePage />;
};
