import React from "react";
import {
  Card,
  CardHeader,
  CardBody,
  Typography,
  Button,
  Dialog,
} from "@material-tailwind/react";
import styles from "./section.module.css";
import { SimpleRegistrationForm } from "../../loginForm/LoginForm";

export function UserCard() {
  const [open, setOpen] = React.useState(false);

  const handleOpen = () => setOpen(!open);
  return (
    <Card
      shadow={false}
      className={`relative grid h-[40rem] w-full max-w-[28rem] items-end justify-center overflow-hidden text-center ${styles.section}`}
    >
      <CardHeader
        floated={false}
        shadow={false}
        color="transparent"
        className="absolute inset-0 m-0 h-full w-full rounded-none bg-[url('https://images.unsplash.com/photo-1552960562-daf630e9278b?ixlib=rb-4.0.3&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=687&q=80')] bg-cover bg-center"
      >
        <div className="to-bg-black-10 absolute inset-0 h-full w-full bg-gradient-to-t from-black/80 via-black/50" />
      </CardHeader>
      <CardBody className="relative py-14 px-6 md:px-12">
        <Typography
          variant="h2"
          color="white"
          className="mb-6 font-medium leading-[1.5]"
        >
          The Solution for every need of Yours!
        </Typography>
        <div>
          <Button onClick={handleOpen} variant="gradient">
            Sign Up
          </Button>
          <Dialog
            open={open}
            handler={handleOpen}
            className="flex justify-center"
          >
            <SimpleRegistrationForm />

                     </Dialog>
        </div>
      </CardBody>
    </Card>
  );
}
