import { createSlice } from '@reduxjs/toolkit';

const filterSlice = createSlice({
  name: 'filter',
  initialState: {
    filtered: {
      job: '',
      location: ''
    }
  },
  reducers: {
    setFiltered: (state, action) => {
      state.filtered = action.payload;
    }
  }
});

export const { setFiltered } = filterSlice.actions;

export default filterSlice.reducer;
