import React, { useState, useEffect } from "react";
import { AddWorkModal } from "../addWorkModal/AddWorkModal";
import { CustomSpinner } from "../loading/Loader";
import { ErrorAlert } from "../error/Error";
import { UserCards } from "../card/UserCard";

export const ProfessionalContent = () => {
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState("");
  const [works, setWorks] = useState([]);

  const getWorks = async () => {
    setIsLoading(true);
    try {
      const response = await fetch(`${process.env.REACT_APP_SERVER_BASE_URL}/works`, {
        method: "GET",
        headers: {
          "Content-type": "application/json",
          authorization: "",
        },
      });
      if (response.ok) {
        const data = await response.json();
        setWorks(data);
        console.log(works)
      } else {
        throw new Error("Something went wrong!");
      }
    } catch (e) {
      setError(e.message);
    } finally {
      setIsLoading(false);
    }
  };

  useEffect(() => {
    getWorks();
  }, []);
console.log(works)
  return (
    <>
      <AddWorkModal />
      <div className="container pt-5 pb-5">
        <div className="row">
          {isLoading && <CustomSpinner />}
          {!isLoading && error && (
            <ErrorAlert message="Oops! Something went wrong while loading data" />
          )}
          {!isLoading && !error && works.length > 0 && works.at[0].map((work) => (
            <div key={work._id} className="col-12 col-md-6 col-lg-4 col-xl-3 mb-3">
              <UserCards
                title={work.title}
                description={work.description}
                img={work.img}
                author={work.author}
              />
            </div>
          ))}
          {!isLoading && !error && works.length === 0 && (
            <p>No works available at the moment.</p>
          )}
        </div>
      </div>
    </>
  );
};
