import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import { isWorkLoading, worksError } from "../redux/WorkCardSlice";
import { useDispatch, useSelector } from "react-redux";
import { CustomSpinner } from "../components/loading/Loader";
import { ErrorAlert } from "../components/error/Error";
import { DetailCard } from "../components/card/DetailCard";

export const DetailPage = () => {
  const [work, setWork] = useState(null);
  const dispatch = useDispatch();
  const isLoading = useSelector(isWorkLoading);
  const error = useSelector(worksError);
  const { _id } = useParams();

  const getDetailWork = async () => {
    try {
      if (!_id) {
        throw new Error("ID del lavoro non fornito");
      }

      const response = await fetch(
        `${process.env.REACT_APP_SERVER_BASE_URL}/works/${_id}`
      );
      const data = await response.json();
      if (data) {
        setWork(data); 
      } else {
        throw new Error("Lavoro non trovato");
      }
    } catch (error) {
      console.error("Errore durante il recupero dei dati del lavoro:", error);
    }
  };

  useEffect(() => {
    getDetailWork();
  }, [_id]);

  return (
    <div className={`flex justify-center items-center h-full`}>
      {isLoading && <CustomSpinner />}
      {!isLoading && error && (
        <ErrorAlert message="Ops! Qualcosa Ã¨ andato storto" />
      )}
      {!isLoading && !error && work && (
        <DetailCard
          className={`w-full max-w-[48rem] flex-row`}
          author={work.author}
          description={work.description}
          title={work.title}
          img={work.img}
          location={work.location}
          pubDate={work.pubDate}
          _id={work._id}
        />
      )}
    </div>
  );
};
