import React, { useState, useEffect } from "react";
import { AddWorkModal } from "../addWorkModal/AddWorkModal";
import { CustomSpinner } from "../loading/Loader";
import { ErrorAlert } from "../error/Error";
import { UserCards } from "../card/UserCard";
import useSession from "../../hooks/useSession";

export const ProfessionalContent = () => {
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState("");
  const [works, setWorks] = useState([]);

  const session = JSON.parse(localStorage.getItem('auth'))
    const isAuthenticated = useSession();

  const getWorks = async () => {
    setIsLoading(true);
    try {
      const response = await fetch(
        `${process.env.REACT_APP_SERVER_BASE_URL}/works`,
        {
          method: "GET",
          headers: {
            "Content-type": "application/json",
            authorization: session,
          },
        }
      );
      if (!response.ok) {
        throw new Error("Something went wrong!");
      }
      const data = await response.json();
      setWorks(data);
    } catch (e) {
      setError(e.message);
    } finally {
      setIsLoading(false);
    }
  };

  useEffect(() => {
    getWorks();
  }, []);

  return (
    <>
      <AddWorkModal />
      <div>
        {isLoading && <CustomSpinner />}
        {!isLoading && error && (
          <ErrorAlert message="Ops! qualcosa Ã¨ andato storto" />
        )}
        {isAuthenticated && !isLoading &&
          !error &&(
          works.works &&
          works.works.map((work) => (
            <div key={work._id}>
              <UserCards
                author={work.author}
                description={work.description}
                title={work.title}
                img={work.img}
              />
            </div>
          )))}
      </div>
    </>
  );
};
