import { useState } from "react";
import { useDispatch } from "react-redux";
import { Button } from "@material-tailwind/react";


export const AddWorkModal = () => {
    const dispatch = useDispatch();
    const [file, setFile] = useState(null);
    const [formData, setFormData] = useState({});
  
    const onChangeHandleFile = (e) => {
      setFile(e.target.files[0]);
    };
  
    const onChangeHandleInput = (e) => {
      const { name, value } = e.target;
      setFormData({
        ...formData,
        [name]: value,
      });
    };
  
    const uploadFile = async () => {
      try {
        const uploadedFile = await dispatch(uploadImage(file)); // Utilizza dispatch per invocare l'azione di upload dell'immagine
        return uploadedFile.source;
      } catch (error) {
        throw new Error("Upload file failed", error.message);
      }
    };
  
    const submitWork = async (e) => {
      e.preventDefault();
      if (file) {
        try {
          const cover = await uploadFile();
          await dispatch(addWork({ ...formData, cover })); // Invia i dati del lavoro con la copertina ottenuta dall'upload
        } catch (error) {
          throw new Error("Submit work failed", error.message);
        }
      } else {
        throw new Error("Something went wrong!");
      }
    };

  return (
    <>
      <Button
        class="select-none rounded-lg bg-gradient-to-tr from-gray-900 to-gray-800 py-3 px-6 text-center align-middle font-sans text-xs font-bold uppercase text-white shadow-md shadow-gray-900/10 transition-all hover:shadow-lg hover:shadow-gray-900/20 active:opacity-[0.85] disabled:pointer-events-none disabled:opacity-50 disabled:shadow-none"
        data-ripple-light="true"
        data-dialog-target="animated-dialog"
      >
        Open Dialog
      </Button>
      <div
        data-dialog-backdrop="animated-dialog"
        data-dialog-backdrop-close="true"
        class="pointer-events-none fixed inset-0 z-[999] grid h-screen w-screen place-items-center bg-black bg-opacity-60 opacity-0 backdrop-blur-sm transition-opacity duration-300"
      >
        <div
          data-dialog="animated-dialog"
          data-dialog-mount="opacity-100 translate-y-0 scale-100"
          data-dialog-unmount="opacity-0 -translate-y-28 scale-90 pointer-events-none"
          data-dialog-transition="transition-all duration-300"
          class="relative m-4 w-2/5 min-w-[40%] max-w-[40%] rounded-lg bg-white font-sans text-base font-light leading-relaxed text-blue-gray-500 antialiased shadow-2xl"
        >
          <div class="flex items-center p-4 font-sans text-2xl antialiased font-semibold leading-snug shrink-0 text-blue-gray-900">
            Its a simple dialog.
          </div>
          <div class="relative p-4 font-sans text-base antialiased font-light leading-relaxed border-t border-b border-t-blue-gray-100 border-b-blue-gray-100 text-blue-gray-500">
            The key to more success is to have a lot of pillows. Put it this
            way, it took me twenty five years to get these plants, twenty five
            years of blood sweat and tears, and I&apos;m never giving up,
            I&apos;m just getting started. I&apos;m up to something. Fan luv.
          </div>
          <div class="flex flex-wrap items-center justify-end p-4 shrink-0 text-blue-gray-500">
            <Button>
              {" "}
              data-ripple-dark="true" data-dialog-close="true" class="px-6 py-3
              mr-1 font-sans text-xs font-bold text-red-500 uppercase
              transition-all rounded-lg middle none center hover:bg-red-500/10
              active:bg-red-500/30 disabled:pointer-events-none
              disabled:opacity-50 disabled:shadow-none"> Cancel
            </Button>
            <Button
              data-ripple-light="true"
              data-dialog-close="true"
              class="middle none center rounded-lg bg-gradient-to-tr from-green-600 to-green-400 py-3 px-6 font-sans text-xs font-bold uppercase text-white shadow-md shadow-green-500/20 transition-all hover:shadow-lg hover:shadow-green-500/40 active:opacity-[0.85] disabled:pointer-events-none disabled:opacity-50 disabled:shadow-none"
            >
              Confirm
            </Button>
          </div>
        </div>
      </div>
    </>
  );
};
