import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { CustomSpinner } from "../loading/Loader";
import { ErrorAlert } from "../error/Error";
import { UserCards } from "../card/UserCard";
import { getAllWorks, allWorks, isWorkLoading, worksError } from "../../redux/WorkCardSlice";
import styles from "./workContent.module.css";
import { DefaultPagination } from "../pagination/Pagination";

export const WorksContent = () => {
  const dispatch = useDispatch();
  const works = useSelector(allWorks);
  const isLoading = useSelector(isWorkLoading);
  const error = useSelector(worksError);
  const [currentPage, setCurrentPage] = useState(1); // Aggiunto stato per la pagina corrente

  useEffect(() => {
    dispatch(getAllWorks());
  }, [dispatch]);

  // Funzione per gestire il cambio di pagina
  const onPageChange = (pageNumber) => {
    setCurrentPage(pageNumber);
    // Puoi inserire qui la logica per caricare i dati della pagina corrente
  };

  return (
    <div className={styles.content}>
      {isLoading && <CustomSpinner />}
      {!isLoading && error && <ErrorAlert message="Ops! Qualcosa è andato storto" />}
      {!isLoading && !error && (
        works.payload &&
        works.payload.map((work) => (
          <div key={work._id}>
            <UserCards
              className={`${styles.card} size-24`}
              author={work.author}
              description={work.description}
              title={work.title}
              img={work.img}
              location={work.location}
              pubDate={work.pubDate}
            />
          </div>
        ))
      )}
      {/* Passa la funzione onPageChange al componente DefaultPagination */}
      <DefaultPagination currentPage={currentPage} onPageChange={onPageChange} />
    </div>
  );
};
