import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { CustomSpinner } from "../loading/Loader";
import { ErrorAlert } from "../error/Error";
import { UserCards } from "../card/UserCard";
import { getAllWorks } from "../../redux/WorkCardSlice";
import styles from "./workContent.module.css";
import { DefaultPagination } from "../pagination/Pagination";
import { setFiltered } from "../../redux/FilterSlice";

export const WorksContent = () => {
  const dispatch = useDispatch();
  const isLoading = useSelector((state) => state.works.isLoading);
  const error = useSelector((state) => state.works.error);
  const works = useSelector((state) => state.works.works);
  const totalPages = useSelector((state) => state.works.totalPages);
  const filters = useSelector((state) => state.filter.filtered);

  const [page, setPage] = useState(1);

  useEffect(() => {
    dispatch(getAllWorks({ page, filters }));
    window.scrollTo(0, 0);
  }, [dispatch, page, filters]);

  const onPageChange = (newPage) => {
    setPage(newPage);
  };

  const handleFilterChange = (filterData) => {
    dispatch(setFiltered(filterData));
  };

  return (
    <div className={`${styles.content}`}>
      {isLoading && <CustomSpinner />}
      {!isLoading && error && <ErrorAlert message="Ops! Qualcosa è andato storto" />}
      {!isLoading && !error && (
        works.map((work) => (
          <div key={work._id}>
            <UserCards
              className={`${styles.card}`}
              author={work.author}
              description={work.description}
              title={work.title}
              img={work.img}
              location={work.location}
              pubDate={work.pubDate}
              _id={work._id}
            />
          </div>
        ))
      )}

      <DefaultPagination
        onPageChange={onPageChange}
        currentPage={page}
        totalPage={totalPages}
        onFilterChange={handleFilterChange} 
      />
    </div>
  );
};
