import { useState } from "react";

export const AddWorkModal = () => {
    const [file, setFile] = useState(null)
    const [formData, setFormData] = useState({})
    console.log(file)
    console.log(formData)

    const onChangeHandleFile = (e) => {
        setFile(e.target.files[0])
    }

    const onChangeHandleInput = (e) => {
        const { name, value } = e.target
        setFormData({
            ...formData,
            [name]: value,
        })
    }

    const uploadFile = async () => {
        const fileData = new FormData();
        fileData.append('uploadImg', file)

        try {
            const response = await fetch(`${process.env.REACT_APP_SERVER_BASE_URL}/books/cloudUploadImg`, {
                method: 'POST',
                body: fileData
            })
            if(response.ok){
            return await response.json()
            }else{
                throw new Error("Somethinks wrong!")
            }
        } catch (e) {
            alert("Upload file failed",e.message)
        }
    }

    const submitBook = async (e) => {
        e.preventDefault();
        if (file) {
            try {
                const uploadedFile = await uploadFile(file)
                const bodyToSend = {
                    ...formData,
                    cover: uploadedFile.source
                }
                const response = await fetch(`${process.env.REACT_APP_SERVER_BASE_URL}/books/createBook`,{
                    method: 'POST',
                    headers: {
                        'Content-Type': 'application/json'
                    },
                    body: JSON.stringify(bodyToSend)
                })
                return await response.json()
            } catch (e) {
                alert("Submit book failed",e.message)
            }
        }else{
            throw new Error ("Somethinks wrong!")
        }
    }
  return (
    <>
