import React ,{useState,useEffect}from "react";
import { Button, IconButton } from "@material-tailwind/react";
import { ArrowRightIcon, ArrowLeftIcon } from "@heroicons/react/24/outline";
import { jwtDecode } from "jwt-decode";

export function DefaultPagination({ currentPage, totalPage, onPageChange }) {
  const session = localStorage.getItem("auth");
  const decodedSession=jwtDecode(session);
  const [page, setPage] = useState(1);
  const [works, setWorks] = useState([]);
  const [totalPages, setTotalPages] = useState(0);

  const getpagination = async () => {
    try {
      const response = await fetch(`${process.env.REACT_APP_SERVER_BASE_URL}/${decodedSession.role}/${decodedSession._id}?page=${page}`,{
        method: 'GET',
        headers: {
            "Content-type": 'application/json',
            "authorization": decodedSession
        }
    });
      const data = await response.json();
      setWorks(data.payload); 
      setTotalPages(data.totalPages); 
    } catch (error) {
      alert("Error fetching works:", error);
    }
  };

  useEffect(() => {
    getpagination();
    window.scrollTo(0, 0);
    
  }, [page]);
  const prev = () => {
    if (currentPage === 1) return;
    onPageChange(currentPage - 1);
  };

  const next = () => {
    if (currentPage === totalPage) return;
    onPageChange(currentPage + 1);
  };
  console.log(currentPage);
  console.log(totalPage);

  return (
    <div className="flex items-center gap-4">
      <Button
        variant="text"
        className="flex items-center gap-2"
        onClick={prev}
        disabled={currentPage === 1}
      >
        <ArrowLeftIcon strokeWidth={2} className="h-4 w-4" /> Previous
      </Button>
      <div className="flex items-center gap-2">
        {Array.from({ length: totalPage }, (_, index) => (
          <IconButton
            key={index + 1}
            variant={currentPage === index + 1 ? "filled" : "text"}
            color="gray"
            onClick={() => onPageChange(index + 1)}
          >
            {index + 1}
          </IconButton>
        ))}
      </div>
      <Button
        variant="text"
        className="flex items-center gap-2"
        onClick={next}
        disabled={currentPage === totalPage}
      >
        Next <ArrowRightIcon strokeWidth={2} className="h-4 w-4" />
      </Button>
    </div>
  );
}
