import React, { useState } from "react";
import { Card, Input, Button, Typography } from "@material-tailwind/react";
import { useNavigate } from 'react-router-dom';
import {jwtDecode} from "jwt-decode";
import useSession from "../../hooks/useSession";

export function LoginForm() {
  const [formData, setFormData] = useState({});
  const [openModal, setOpenModal] = useState(false);
  const navigate = useNavigate();
  const session = useSession();

  const onSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch(
        `${process.env.REACT_APP_SERVER_BASE_URL}/login`,
        {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify(formData),
        }
      );

      if (response.ok) {
        const data = await response.json();
        if (data.token) {
          localStorage.setItem('auth', data.token);
          navigateBasedOnRole(data.token);
        } else {
          throw new Error('Token not found in response data');
        }
      } else {
        throw new Error('HTTP request failed with status ' + response.status);
      }
    } catch (error) {
      alert('Failed to login: ' + error.message);
    }
  };

  const onChangeInput = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });
  };

  const handleOpenModal = () => {
    setOpenModal(true);
  };

  const handleCloseModal = () => {
    setOpenModal(false);
  };

  const navigateBasedOnRole = (token) => {
    const role = session ? session.role : jwtDecode(token).role;
    if (role === 'user') {
      navigate('/user');
    } else if (role === 'professional') {
      navigate('/professional');
    } else {
      alert('Unknown role');
    }
  };

  return (
    <div>
      <Button onClick={handleOpenModal}>Login</Button>
      {openModal && (
        <Card color="transparent" shadow={false}>
          <Typography variant="h4" color="blue-gray">
            Log in
          </Typography>
          <Typography color="gray" className="mt-1 font-normal">
            Welcome back. Enter your details to log in.
          </Typography>
          <form onSubmit={onSubmit} className="mt-8 mb-2 w-80 max-w-screen-lg sm:w-96">
            <div className="mb-1 flex flex-col gap-6">
              <Typography variant="h6" color="blue-gray" className="-mb-3">
                Your Email
              </Typography>
              <Input
                onChange={onChangeInput}
                name="email"
                size="lg"
                placeholder="name@mail.com"
                className=" !border-t-blue-gray-200 focus:!border-t-gray-900"
                labelProps={{
                  className: "before:content-none after:content-none",
                }}
              />
              <Typography variant="h6" color="blue-gray" className="-mb-3">
                Password
              </Typography>
              <Input
                onChange={onChangeInput}
                name="password"
                type="password"
                size="lg"
                placeholder="********"
                className=" !border-t-blue-gray-200 focus:!border-t-gray-900"
                labelProps={{
                  className: "before:content-none after:content-none",
                }}
              />
            </div>
            <Button type="submit" className="mt-6" fullWidth>
              Sign in
            </Button>
            <Button onClick={handleCloseModal} className="mt-4" fullWidth>
              Cancel
            </Button>
          </form>
        </Card>
      )}
    </div>
  );
}
