import React from "react";
import { Button, IconButton } from "@material-tailwind/react";
import { ArrowRightIcon, ArrowLeftIcon } from "@heroicons/react/24/outline";
import { useSelector } from "react-redux";
import { selectCurrentPage, selectTotalPage } from "../../redux/WorkCardSlice";

export function DefaultPagination({ onPageChange }) {
  const currentPage = useSelector(selectCurrentPage);
  const totalPage = useSelector(selectTotalPage);

  const prev = () => {
    if (currentPage > 1) {
      onPageChange(currentPage - 1);
    }
  };

  const next = () => {
    if (currentPage < totalPage) {
      onPageChange(currentPage + 1);
    }
  };

  const goToPage = (page) => {
    onPageChange(page);
  };

  // Verifica se totalPage è valido
  if (!Number.isInteger(totalPage) || totalPage <= 0) {
    return null; // Se totalPage non è valido, non renderizzare nulla
  }

  return (
    <div className="flex items-center gap-4">
      <Button
        variant="text"
        className="flex items-center gap-2"
        onClick={prev}
        disabled={currentPage === 1}
      >
        <ArrowLeftIcon strokeWidth={2} className="h-4 w-4" /> Previous
      </Button>
      <div className="flex items-center gap-2">
        {[...Array(totalPage)].map((_, index) => (
          <IconButton
            key={index + 1}
            variant={currentPage === index + 1 ? "filled" : "text"}
            color="gray"
            onClick={() => goToPage(index + 1)}
          >
            {index + 1}
          </IconButton>
        ))}
      </div>
      <Button
        variant="text"
        className="flex items-center gap-2"
        onClick={next}
        disabled={currentPage === totalPage}
      >
        Next <ArrowRightIcon strokeWidth={2} className="h-4 w-4" />
      </Button>
    </div>
  );
}
