import React,{useState,useEffect} from "react";
import { MainLayout } from "../layout/MainLayout";
import { DefaultPagination } from "../components/pagination/Pagination";
import { jwtDecode } from "jwt-decode";

const MyWorks = () => {
  const [page, setPage] = useState(1);
  const [works, setWorks] = useState([]);
  const [totalPages, setTotalPages] = useState(0);

  const session=jwtDecode(localStorage.getItem("auth"))

  const getMyWorks = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch(
        `${process.env.REACT_APP_SERVER_BASE_URL}/${session.role}/${session._id}/myWorks?page=${page}`,
        {
          method: "GET",
          headers: {
            "Content-type": "application/json",
            authorization: session,
          },
        }
      );
      const data = await response.json();
      setWorks(data.payload);
      setTotalPages(data.totalPages);
    } catch (error) {
      alert("Error fetching works:", error);
    }
  };
  useEffect(() => {
    getMyWorks();
    window.scrollTo(0, 0);
  }, [page]);
  const onPageChange = (newPage) => {
    setPage(newPage);
  };
  return (
    <MainLayout>
      <div>MyWorks</div>
      <DefaultPagination
        onPageChange={onPageChange}
        currentPage={page}
        totalPage={totalPages}
      />
    </MainLayout>
  );
};

export default MyWorks;
