import { createAsyncThunk, createSlice } from "@reduxjs/toolkit";
import AxiosClient from "../client/client";

const client = new AxiosClient();

const initialState = {
  works: [],
  isLoading: false,
  error: null,
  totalWorks: 0,
  currentPage: 1,
  pageSize: 4,
};

export const getAllWorks = createAsyncThunk(
  "work/GETWORKS",
  async (page) => {
    try {
      const response = await client.get(`/works?page=${page}`);
      return response.data; // Assumendo che i dati delle opere siano contenuti in response.data
    } catch (error) {
      throw error;
    }
  }
);

const worksSlice = createSlice({
  name: "works",
  initialState,
  extraReducers: (builder) => {
    builder
      .addCase(getAllWorks.pending, (state) => {
        state.isLoading = true;
      })
      .addCase(getAllWorks.fulfilled, (state, action) => {
        state.isLoading = false;
        state.totalWorks = action.payload.totalWorks; // Assumendo che il totale delle opere sia disponibile in action.payload.totalWorks
        state.works = action.payload.payload; // Assumendo che i dati delle opere siano disponibili in action.payload.payload
      })
      .addCase(getAllWorks.rejected, (state) => {
        state.isLoading = false;
        state.error = "Ops, an error has occurred.";
      });
  },
});

export const allWorks = (state) => state.worksData.works;
export const isWorkLoading = (state) => state.worksData.isLoading;
export const worksError = (state) => state.worksData.error;
export default worksSlice.reducer;
